// Helper function to fetch game images from RAWG API
// Get a free API key at https://rawg.io/apidocs
// This would typically be called by the Discord bot when creating a challenge
const fetchGameImage = async (gameName, apiKey = 'YOUR_RAWG_API_KEY') => {
  try {
    const searchUrl = `https://api.rawg.io/api/games?key=${apiKey}&search=${encodeURIComponent(gameName)}&page_size=1`;
    const response = await fetch(searchUrl);
    const data = await response.json();
    
    if (data.results && data.results.length > 0) {
      return data.results[0].background_image; // Returns the game's background image URL
    }
    return null;
  } catch (error) {
    console.error('Error fetching game image:', error);
    return null;
  }
};

