// Timer display component
const TimerDisplay = ({ deadline, submissions }) => {
  const [timeLeft, setTimeLeft] = useState('');
  const [urgencyPercent, setUrgencyPercent] = useState(100);
  
  useEffect(() => {
    if (!deadline) return;
    
    const updateTimer = () => {
      const now = new Date();
      const end = new Date(deadline);
      const diff = end - now;
      
      if (diff <= 0) {
        setTimeLeft('Expired');
        setUrgencyPercent(0);
        return;
      }
      
      // Calculate max time based on submissions (for gradient calculation)
      // 2 subs = 30 days, 3 subs = 21 days, 4 subs = 14 days
      const maxDays = submissions === 2 ? 30 : submissions === 3 ? 21 : 14;
      const maxMs = maxDays * 24 * 60 * 60 * 1000;
      const percent = Math.min(100, Math.max(0, (diff / maxMs) * 100));
      setUrgencyPercent(percent);
      
      const days = Math.floor(diff / (1000 * 60 * 60 * 24));
      const hours = Math.floor((diff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
      
      if (days > 0) {
        setTimeLeft(`${days}d ${hours}h`);
      } else {
        const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
        setTimeLeft(`${hours}h ${minutes}m`);
      }
    };
    
    updateTimer();
    const interval = setInterval(updateTimer, 60000);
    return () => clearInterval(interval);
  }, [deadline, submissions]);

  if (submissions < 2) {
    return <span className="text-gray-500 text-sm italic">Timer starts after 2nd submission...</span>;
  }

  // Calculate color gradient from green (100%) through yellow (50%) to red (0%)
  const getGradientColor = (percent) => {
    if (percent > 50) {
      // Green to Yellow (100% -> 50%)
      const ratio = (percent - 50) / 50;
      const r = Math.round(234 - (234 - 34) * ratio); // 234 (yellow) to 34 (green)
      const g = Math.round(179 + (197 - 179) * ratio); // 179 (yellow) to 197 (green)
      const b = Math.round(8 + (94 - 8) * ratio); // 8 (yellow) to 94 (green)
      return `rgb(${r}, ${g}, ${b})`;
    } else {
      // Yellow to Red (50% -> 0%)
      const ratio = percent / 50;
      const r = Math.round(239 + (234 - 239) * ratio); // 239 (red) to 234 (yellow)
      const g = Math.round(68 + (179 - 68) * ratio); // 68 (red) to 179 (yellow)
      const b = Math.round(68 + (8 - 68) * ratio); // 68 (red) to 8 (yellow)
      return `rgb(${r}, ${g}, ${b})`;
    }
  };

  const timerColor = getGradientColor(urgencyPercent);

  return (
    <div className="flex items-center gap-2" style={{ color: timerColor }}>
      <span className="text-lg"></span>
      <span className="font-mono font-bold">{timeLeft}</span>
    </div>
  );
};

