// Format score based on type
const formatScore = (score, type) => {
  if (type === 'fastest' || type === 'slowest') {
    return `${score}s`;
  }
  if (typeof score === 'number' && score >= 1000) {
    return score.toLocaleString();
  }
  return score;
};

// Custom tooltip for the chart
const CustomTooltip = ({ active, payload, label }) => {
  if (active && payload && payload.length) {
    return (
      <div className="bg-[#1a1a24] border border-[#333340] rounded-lg p-3 shadow-xl">
        <p className="text-gray-400 text-sm mb-2">{label}</p>
        {payload
          .sort((a, b) => b.value - a.value)
          .map((entry, index) => (
            <div key={index} className="flex items-center gap-2 text-sm">
              <div 
                className="w-3 h-3 rounded-full" 
                style={{ backgroundColor: entry.color }}
              />
              <span className="text-white capitalize">{entry.name}:</span>
              <span className="text-gray-300 font-mono">{entry.value} pts</span>
            </div>
          ))}
      </div>
    );
  }
  return null;
};

