                  const daysLeft = Math.floor(diff / (1000 * 60 * 60 * 24));
                  const hoursLeft = Math.floor((diff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
                  
                  // Calculate urgency percent (assuming max 30 days)
                  const maxMs = 30 * 24 * 60 * 60 * 1000;
                  const urgencyPercent = Math.min(100, Math.max(0, (diff / maxMs) * 100));
                  
                  return { ...c, daysLeft, hoursLeft, diff, urgencyPercent };
